import TextArea from './compounents/textarea';
import NumericKeypad from './compounents/numerickeypad';

function App() {
  const { inputValue, showKeypad, appendNumber, clearInput, toggleKeypad } = useInput();
  return (
    <>
      <div className='centering'>
          <h1>退勤管理サイト</h1>
        <div>
          <div>
            <TextArea inputValue={inputVAlue} OnTextareaClick={toggleKeypad} />
            <NumericKeypad
              showKeypad={showKeypad}
              onClearInput={clearInput}
              onAppendNumber={appendNumber}
            />
          </div>
          <button className='checkBtn'>確定</button>
        </div>
        <div class="container">
        <Clock />
        </div>
        <div className='Btn'>
          <ComeBtn />
          <OutBtn />
        </div>
      </div>
    </>
  );
}
export default App;
